<?php vF_Check();# ----------------------# Class: Language# Author: Yplit# Date: 14/6/2012#-----------------------class vF_config{	private $_instance;	global $vF;	public $defaultConfigFile = 'config.php';	public $defaultTablePreFix = 'vF_';	public $defaultForumName = 'vForum';	public $version = '0.1';	public $revision = 10;	public function __construct()	{		$this->vF = $GLOBALS['vF'];	}	public static function getInstance()	{		if( !self::$_instance )		{			self::$_instance = new self();		}		return self::$_instance;	}	public static function loadConfig( $configFile )	{		if( empty( $configFile ) OR !file_exists( $configFile ) )		{			self::_redirectInstall();		}		require( $configFile );		if( !$vF_Config OR !is_object( $vF_Config ) )		{			self::_redirectInstall();		}		global $vF_Config;	}	private static function _redirectInstall()	{		@header( "Location: " . vF_url::getInstance()->parseUrl( 'install.php' ) );		exit;	}}