<?php vF_Check();# ----------------------# Class: Get System Option# Author: Yplit# Date: 13/6/2012#-----------------------class vF_getOptions{	private $_instance;	public $allow_cache = true;	private $cache_file = '';	private $cache_content = '';	public $options;	public function __construct()	{		if( $this->allow_cache )		{			$this->cache_file = vF_DIR . '/vF_Resource/cache/options.' . vF_hash::getInstance()->hash( $vF->system->key ) . '.php'; 			if( file_exists( $this->cache_file ) and is_readable( $this->cache_file ) )			{				$this->cache_content = @file_get_contents( $this->cache_file );			}		}	}	public static function getInstance()	{		if( !self::$_instance )		{			self::$_instance = new self();		}		return self::$_instance;	}	public function get()	{		if( $this->allow_cache and !empty( $this->cache_content ) )		{			$tmp = unserialize( $this->cache_content );			if( is_array( $tmp ) )			{				return vF_Array2Object::getInstance()->convert( $tmp );			}		}	}	public function setCacheFile( $file )	{		$this->cache_file = $file;	}	public function disableCache()	{		$this->allow_cache = false;	}	public function activeCache()	{		$this->allow_cache = true;	}}