<?php vF_Check();class vF_templateCompilerStatementRaw extends vF_templateCompilerStatement{	public function getFullStatements( $outputVar )	{		$output = '';		foreach ($this->_statements AS $statement)		{			if( is_string( $statement ) )			{				$output .= $statement;			}			else			{				$output .= $statement->getFullStatements( $outputVar );			}		}		return $output;	}}