<?php vF_Check();# ----------------------# Class: Install# Author: Yplit# Date: 13/6/2012#-----------------------class vF_install{	private $_instance;	public function __construct()	{	}	public static function getInstance()	{		if( !self::$_instance )		{			self::$_instance = new self();		}		return self::$_instance;	}	public function process()	{		$step = vF_input::getInstance()->getIntParam( 'step', 'r', 0 );		/*		* Step 0: Read licsense		* Step 1: Config file		* Step 2: Database info		* Step 3: Forum Info		* Step 5: Adding database process		* Step 6: Welcome to new Forum!		*/		if( $step > 6 ) $step = 0;		require( vF_DIR . '/vF_Core/install/step/vF_step_' . $step . '.php' );	}}